package com.character.main;

public class MethodImplementation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Character.isLetter('d'));
		System.out.println(Character.isLetter('8'));
		System.out.println(Character.isDigit('d'));
		System.out.println(Character.isDigit('8'));
		System.out.println(Character.isWhitespace('d'));
		System.out.println(Character.isWhitespace(' '));
		System.out.println(Character.isWhitespace('\n'));
		System.out.println(Character.isWhitespace('\t'));
		System.out.println(Character.isWhitespace(9));
		System.out.println(Character.isWhitespace('9'));
		System.out.println(Character.isUpperCase('A'));
		System.out.println(Character.isUpperCase('a'));
		System.out.println(Character.isUpperCase(65));
		//System.out.println(Character.isWhitespace(' '));
	}

}
