package com.string.concatenation;

public class ConcatMethod {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="Himani";
		String s1=" Kumari";
		System.out.println(s.concat(s1));
		
		//concat at first
		String str="kumari";
		str="Himani".concat(str);
		System.out.println(str);
	}

}
