package com.string.concatenation;

public class UsingJoinMethod {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="Himani";
		String s1=" Kumari";
		System.out.println(String.join("",s,s1));
		
		String str=null;
		System.out.println(str.join(null,"csd","heu"));
	}

}
