package com.string.method;

public class LastIndexOfMethod {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="Hi my name is himani kumari";
		System.out.println(s.lastIndexOf('m'));
		System.out.println(s.lastIndexOf('m',7));
		System.out.println(s.lastIndexOf("himani"));
		System.out.println(s.lastIndexOf("himani",5));
	}

}
